roi=$1
lhz=$2
lhp=$3
rhz=$4
rhp=$5

echo "<tr> <td.align="center"> </td> </tr> <td> <td.align="center"_bgcolor=#FF0000> <td.align="center"_bgcolor=#00FFFF>" | awk '{ if ("'${lhz}'">=2.0 && "'${rhz}'">=2.0) printf "\t%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t%s\n", $1, $5, "'${roi}'", $3, $7, "'${lhz}'", $3, $7, "'${lhp}'", $3, $7, "'${rhz}'", $3, $7, "'${rhp}'", $3, $4; else if ("'${lhz}'">=2.0 && "'${rhz}'"<2.0) printf "\t%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t%s\n", $1, $5, "'${roi}'", $3, $7, "'${lhz}'", $3, $7, "'${lhp}'", $3, $2, "'${rhz}'", $3, $2, "'${rhp}'", $3, $4; else if ("'${lhz}'"<2.0 && "'${rhz}'">=2.0) printf "\t%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t%s\n", $1, $5, "'${roi}'", $3, $2, "'${lhz}'", $3, $2, "'${lhp}'", $3, $7, "'${rhz}'", $3, $7, "'${rhp}'", $3, $4; else if ((("'${lhz}'"+2.0) <= 0) && (("'${rhz}'"+2.0) <= 0)) printf "\t%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t%s\n", $1, $5, "'${roi}'", $3, $6, "'${lhz}'", $3, $6, "'${lhp}'", $3, $6, "'${rhz}'", $3, $6, "'${rhp}'", $3, $4; else if ((("'${lhz}'"+2.0) <= 0) && (("'${rhz}'"+2.0) >= 0)) printf "\t%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t%s\n", $1, $5, "'${roi}'", $3, $6, "'${lhz}'", $3, $6, "'${lhp}'", $3, $2, "'${rhz}'", $3, $2, "'${rhp}'", $3, $4; else if ((("'${lhz}'"+2.0) >= 0) && (("'${rhz}'"+2.0) <= 0)) printf "\t%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t%s\n", $1, $5, "'${roi}'", $3, $2, "'${lhz}'", $3, $2, "'${lhp}'", $3, $6, "'${rhz}'", $3, $6, "'${rhp}'", $3, $4; else printf "\t%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t\t%s%s%s\n\t%s\n", $1, $5, "'${roi}'", $3, $2, "'${lhz}'", $3, $2, "'${lhp}'", $3, $2, "'${rhz}'", $3, $2, "'${rhp}'", $3, $4}' | sed 's/.align/ align/g' | sed 's/_bg/ bg/g'

